mapper.update:
    UPDATE REPORT_INFO
		SET
		 REPORT_NAME    =  #{REPORT_NAME}
		,REPORT_TYPE    =  #{REPORT_TYPE}
		,COMPANY_ID     =  #{COMPANY_ID}
		,PROJECT_NAME   =  #{PROJECT_NAME}
    WHERE REPORT_ID=#{REPORT_ID}

mapper.delete:
		DELETE FROM REPORT_INFO 
		WHERE REPORT_ID IN
		
            #{item}
