mapper.select:
SELECT A.*,B.USER_NAME FROM (SELECT FAQ_ID ,DATE_FORMAT(FAQ_DT,'%Y-%m-%d %H:%i:%s') AS FAQ_DT ,USER_ID ,FAQ_TYPE ,CASE WHEN FAQ_TYPE=0 THEN '운영 FAQ' ELSE '기타 FAQ' END AS FAQ_TYPE_NM ,FAQ_TITLE ,CONTENT FROM FAQ_INFO WHERE 1=1
AND FAQ_TYPE IN
${item}
AND FAQ_DT >= CURDATE() - INTERVAL #{searchRadio} MONTH
AND FAQ_DT >= #{sDate} AND FAQ_DT <= #{eDate}
) A LEFT JOIN USER_INFO B ON(A.USER_ID=B.USER_ID) WHERE 1=1
AND A.FAQ_TITLE LIKE CONCAT('%',#{searchValue},'%')
AND B.USER_NAME LIKE CONCAT('%',#{searchValue},'%')
ORDER BY A.FAQ_DT DESC

mapper.update:
    UPDATE FAQ_INFO
		SET
		FAQ_TYPE      = #{FAQ_TYPE}
		
			 ,USER_ID          = #{USER_ID}
		
		,FAQ_TITLE     = #{FAQ_TITLE}
		,CONTENT          = #{CONTENT}
    WHERE FAQ_ID=#{FAQ_ID}

mapper.delete:
		DELETE FROM FAQ_INFO 
		WHERE FAQ_ID IN
		
            #{item}
