mapper.insert:
    
    
	    SELECT IFNULL(MAX(BILL_PRIOR),0)+1 AS BILL_PRIOR FROM BILL_FILE
	
    
	INSERT INTO BILL_FILE(
		 BILL_FILE_ID
		,BILL_NAME
		,BILL_PRIOR
		,REG_DT
	)
	VALUES (
		 #{BILL_FILE_ID}
		,#{BILL_NAME}
		,#{BILL_PRIOR}
		,curdate()
	)

mapper.update:
    
    
	    SELECT BILL_PRIOR FROM BILL_FILE WHERE BILL_FILE_ID=#{BILL_FILE_ID}
	
    
    UPDATE BILL_FILE
		SET BILL_PRIOR     = BILL_PRIOR + 1
		WHERE BILL_PRIOR  <  #{BILL_PRIOR}
    ;
    UPDATE BILL_FILE
		SET BILL_PRIOR     = 1
    WHERE BILL_FILE_ID=#{BILL_FILE_ID}

    UPDATE BILL_FILE
		SET
		 BILL_NAME      = #{BILL_NAME}
    WHERE BILL_FILE_ID=#{BILL_FILE_ID}

mapper.delete:
		DELETE FROM BILL_FILE 
		WHERE BILL_FILE_ID IN
		
            #{item}

mapper.select:
SELECT T1.*,T2.* FROM ( SELECT J1.*,J2.COMPANY_NAME FROM ( SELECT A.BILL_ID ,A.USER_ID ,A.COMPANY_ID ,A.SND_DT ,A.RCV_DT ,A.BILL_PRICE ,A.BILL_MN_FILE ,B.USER_NAME FROM bill_snd A LEFT JOIN (SELECT USER_ID AS TID, USER_NAME FROM USER_INFO) B ON(A.USER_ID=B.TID) )J1 LEFT JOIN (SELECT COMPANY_ID AS TTID, COMPANY_NAME FROM COMPANY_INFO) J2 ON(J1.COMPANY_ID=J2.TTID) )T1 LEFT JOIN (SELECT FILE_ID, FILE_NAME,FILE_ORIGIN FROM FILE_INFO)T2 ON(T1.BILL_ID=T2.FILE_ORIGIN) WHERE 1= 1
and COMPANY_ID=#{COMPANY_ID}
AND SND_DT >= CURDATE() - INTERVAL #{searchRadio2} MONTH
AND SND_DT >= #{sDate} AND SND_DT <= #{eDate}
AND COMPANY_NAME LIKE CONCAT('%',#{searchValue},'%')
