mapper.select:
SELECT A.*,B.USER_NAME FROM (SELECT NOTICE_ID ,DATE_FORMAT(NOTICE_DT,'%Y-%m-%d') AS NOTICE_DT ,USER_ID ,NOTICE_TYPE ,CASE WHEN NOTICE_TYPE=0 THEN '작업공지' WHEN NOTICE_TYPE=1 THEN '기타공지' ELSE '장애공지' END AS NOTICE_TYPE_NM ,NOTICE_TITLE ,CONTENT ,FLAG_EMAIL ,FLAG_SMS FROM NOTICE_info WHERE 1=1
AND NOTICE_TYPE IN
${item}
AND NOTICE_DT >= CURDATE() - INTERVAL #{searchRadio} MONTH
AND NOTICE_DT >= #{sDate} AND NOTICE_DT <= #{eDate}
) A LEFT JOIN USER_INFO B ON(A.USER_ID=B.USER_ID) WHERE 1=1
AND A.NOTICE_ID LIKE CONCAT('%',#{searchValue},'%')
AND NOTICE_TITLE LIKE CONCAT('%',#{searchValue},'%')
AND B.USER_NAME LIKE CONCAT('%',#{searchValue},'%')
ORDER BY A.NOTICE_DT DESC

mapper.update:
    UPDATE NOTICE_INFO
		SET
		NOTICE_TYPE      = #{NOTICE_TYPE}
		
			 ,USER_ID          = #{USER_ID}
		
		,NOTICE_TITLE     = #{NOTICE_TITLE}
		,CONTENT          = #{CONTENT}
		,FLAG_EMAIL       = IFNULL(#{FLAG_EMAIL},'N')
		,FLAG_SMS         = IFNULL(#{FLAG_SMS},'N')
    WHERE NOTICE_ID=#{NOTICE_ID}

mapper.delete:
		DELETE FROM NOTICE_INFO 
		WHERE NOTICE_ID IN
		
            #{item}
